import { useCallback, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./Frame3.module.css";

const Frame3 = () => {
  const navigate = useNavigate();

  const onRectangleImage3Click = useCallback(() => {
    navigate("/frame-6");
  }, [navigate]);

  const [orderData, setOrderData] = useState([]);
  const [currentDate, setCurrentDate] = useState('');

  useEffect(() => {
    // Function to fetch order data from the server
    const fetchOrderData = () => {
      fetch('/order') // Replace with your server's API endpoint
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then((data) => {
          setOrderData(data);
        })
        .catch((error) => {
          console.error('Error fetching orders:', error);
        });
    };

    // Fetch data initially and set the current date
    fetchOrderData();
    updateCurrentDate();

    // Fetch data every second
    const intervalId = setInterval(() => {
      fetchOrderData();
      updateCurrentDate();
    }, 1000);

    // Clear the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, []);

  const updateCurrentDate = () => {
    const now = new Date();
    const day = now.getDate().toString().padStart(2, '0');
    const month = (now.getMonth() + 1).toString().padStart(2, '0');
    const year = now.getFullYear().toString().slice(-2);
    setCurrentDate(`${day}/${month}/${year}`);
  };

  return (
    <div className={styles.rectangleParent}>
      {/* ...rest of your component code... */}
      <div className={styles.date}>{`Date: ${currentDate}`}</div>
      {/* ...rest of your component code... */}
    </div>
  );
};

export default Frame3;
