import { useCallback, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./Frame3.module.css";

const Frame3 = () => {
  const navigate = useNavigate();
  const onRectangleImage3Click = useCallback(() => {
    navigate("/frame-6");
  }, [navigate]);
  const onCHECKOUTTextClick = useCallback(() => {
    navigate("/frame-6");
  }, [navigate]);

  const [orderData, setOrderData] = useState([]);
  const [currentDate, setCurrentDate] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const fetchOrderData = async () => {
      try {
        const response = await fetch('http://127.0.0.1:6969/order');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setOrderData(data);
        const total = data.reduce((acc, order) => acc + (order.quantity * order.price), 0);
        setTotalPrice(total);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrderData();
    updateCurrentDate();

    // Consider re-enabling this if you need regular updates
    // const intervalId = setInterval(() => {
    //   fetchOrderData();
    //   updateCurrentDate();
    // }, 10000); // Fetch every 10 seconds

    // return () => clearInterval(intervalId);
  }, []);

  const updateCurrentDate = () => {
    const now = new Date();
    const day = now.getDate().toString().padStart(2, '0');
    const month = (now.getMonth() + 1).toString().padStart(2, '0');
    const year = now.getFullYear().toString();
    setCurrentDate(`${day}/${month}/${year}`);
  };

  return (
    <div className={styles.rectangleParent}>
      {/* ... your existing JSX ... */}
      <div className={styles.frameChild}>
        <table className={styles.scrollableTableContainer}>
          <thead>
            <tr className="head">
              <th>Name</th>
              <th>Quantity</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {orderData.map((order, index) => (
              <tr key={index} className={styles.trow}>
                <td>{order.name}</td>
                <td>{order.quantity}</td>
                <td>{order.price}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Frame3;
