import { useCallback, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Frame3.module.css';

const Frame3 = () => {
  const navigate = useNavigate();
  const [orderData, setOrderData] = useState([]);
  const [currentDate, setCurrentDate] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);

  const onRectangleImage3Click = useCallback(() => {
    navigate('/frame-6');
  }, [navigate]);

  const onCHECKOUTTextClick = useCallback(() => {
    navigate('/frame-6');
  }, [navigate]);

  const updateCurrentDate = () => {
    const now = new Date();
    const day = now.getDate().toString().padStart(2, '0');
    const month = (now.getMonth() + 1).toString().padStart(2, '0');
    const year = now.getFullYear();
    setCurrentDate(`${day}/${month}/${year}`);
  };

  useEffect(() => {
    let isMounted = true;

    const fetchOrderData = async () => {
      try {
        const response = await fetch('http://192.168.31.224:6969/order');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();

        if (isMounted) {
          setOrderData(data);
          const total = data.reduce((acc, order) => acc + order.quantity * order.price, 0);
          setTotalPrice(total);
        }
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrderData();
    updateCurrentDate();

    const intervalId = setInterval(() => {
      if (isMounted) {
        fetchOrderData();
        updateCurrentDate();
      }
    }, 5000); // Reduced frequency to every 5 seconds

    return () => {
      clearInterval(intervalId);
      isMounted = false;
    };
  }, []);

  return (
    <div className={styles.rectangleParent}>
      {/* ... rest of your component ... */}
      <div className={styles.date}>{`Date: ${currentDate}`}</div>
      <div className={styles.subtotal}>Subtotal: à¸¿{totalPrice}</div>
      <table className={styles.scrollableTableContainer}>
        <thead>
          <tr className="head">
            <th>Name</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {orderData.map((order, index) => (
            <tr key={order.id || index} className={styles.trow}>
              <td>{order.name}</td>
              <td>{order.quantity}</td>
              <td>{order.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* ... rest of your component ... */}
    </div>
  );
};

export default Frame3;
